						<text class="number">x {{product.productUnit}}</text>
					</view>
				</view>
			</view>
		</view>

		<!-- 金额明细 -->
		<view class="yt-list">
			<view class="yt-list-cell b-b">
				<text class="cell-tit clamp">商品积分</text>
				<text class="cell-tip">{{order.actualPoint}}积分</text>
